/**
 * Minify the HTML
 * grunt htmlmin
 * @doc https://github.com/kangax/html-minifier
 */
(function (controller) {

  controller.loadTask = function (grunt) {
  	grunt.loadNpmTasks("grunt-contrib-htmlmin");
  };

  controller.loadConfig = function(config){
  	var helperConfig = require("./helper.config");

  	return helperConfig.merge(config, 'htmlmin',{
		   dev : {
          options: { 
            removeEmptyAttributes: true,
            removeEmptyElements: true,
            removeRedundantAttributes: true,
            removeComments: true,
            removeOptionalTags: true,
            collapseWhitespace: true
          },
          files: {
            './tasks/htmlmin/minified/g1.min.html' : ['./tasks/htmlmin/normal/*.html']
          }
       }
	  });

  };


})(module.exports);